select o.order_id, o.order_status as main_order_status, o.resource_status as main_order_resource_status, oi.order_item_id, oi.order_status as suborder_status, oi.resource_status suborder_resource_status, oi.perform_status, oi.visit_time, oi.content, oi.order_memo, r.supplier_id, oi.notintime_flag, oi.buyout_flag, oi.buyout_total_price, oi.buyout_price, oi.buyout_quantity, oi.total_settlement_price, oi.actual_settlement_price, oi.quantity, oi.supp_goods_id, o.info_status as main_order_info_status, oi.info_status as suborder_info_status from ord_order o inner join ord_order_item oi on o.order_id = oi.order_id inner join ORD_EXPIRED_REFUND r on oi.order_item_id = r.order_item_id where (o.category_id = 11 or o.category_id = 12 or o.category_id = 13) and r.process_status = 0
and
oi.order_item_id = #{item}

